import {React,useState} from 'react'
import {Drawer, IconButton,makeStyles, List} from '@material-ui/core'
import CloseIcon from '@material-ui/icons/Close'
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket"


const drawerWidth = '50vw';

const useStyles = makeStyles((theme) => ({
  drawer: {
    width: drawerWidth,
    
  },
  root: {
      width: '100vw',
      height: '100vh',
      display:'grid',
      justifyItems:'center',
      paddingTop:'10vh',
    
  },
  menuicon:{
    order:'1',
    display:'flex',
    flexGrow:1
  },
  drawItems: {
    textDecoration: "none",
   color:'navyblue',
    fontFamily: "Ubuntu",
  }
}));

const Cart = () => {

    const classes = useStyles();

    const onClose=({onClick}) => setopenDrawer(false)
  const [openDrawer, setopenDrawer] = useState(false);

    return (
        <>
        <Drawer
        anchor="Right"
        open={openDrawer}
        
        >
            <IconButton>
            <CloseIcon onClick={onClose}/>
            </IconButton>
            <List className={classes.drawer}>
                No Products to display
            </List>
        </Drawer>
        <IconButton>
       <ShoppingBasketIcon  onClick={() => setopenDrawer(!openDrawer)}/>
        </IconButton>
        </>
    )
}

export default Cart
