import {React,useEffect} from 'react'
import axios from 'axios'
import { useTheme } from '@material-ui/core'


const ProductDetailPage = () => {
    const url = `http://localhost:5000/cards/1`
const [card,setCard] =useTheme(null)  
 let content =null
 useEffect(()=>{
    axios.get(url)
    .then(response=>{
        setCard(response.data)
    })
 },[url])
    
 if(card){
     return(
      content=
             <div>
                 <h1 className="text-2xl font-bold mb-3">
                {card.data.name}
            </h1>
            <div>
                <img
                    src={card.data.images[0].imageUrl}
                    alt={card.data.name}
                />
            </div>
            <div className="font-bold text-xl mb-3">
                $ {card.data.price}
            </div>
            <div>
                {card.data.description}
            </div> 
             </div>
         
     )
 }
    return (
        <div>
             {content}
        </div>
    )
}

export default ProductDetailPage
