import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import "../index.css";
import {
  Button,
  makeStyles,
  Typography,
  useMediaQuery,
  useTheme,
  
} from "@material-ui/core";
import { NavLink } from "react-router-dom";
import DrawerComponent from './DrawerComponent'
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { lightBlue } from "@material-ui/core/colors";

// const drawerComponent=DrawerComponent


const useStyles = makeStyles({
  nav: {
    backgroundColor: "palevioletred",
   display:'grid'
  },
  Logo: {
    fontFamily: "Kaushan Script",
    margin:'auto',
   flexGrow:1
  },
  navItems: {
    textDecoration: "none",
    paddingRight: "15px",
    paddingLeft: "15px",
    color: "#fefefe",
    fontFamily: "Ubuntu",
  },
  btn:{
    fontFamily:'Ubuntu',
    color:lightBlue,
    fontWeight:600,
    display:'flex'
  }
});

const Navbar = () => {

  // const [drawerComponent, setdrawerComponent] = useState()

  const classes = useStyles();

  // responsive breaker points

  const theme = useTheme();

  const isMatch = useMediaQuery(theme.breakpoints.down("sm"));

  console.log(theme);
  return (
    <div>
      <AppBar className={classes.nav}>
        <Toolbar>
          {isMatch ? (
            <DrawerComponent/>
          ) : (
            <>
              <NavLink className={classes.navItems} to="/">
                Home
              </NavLink>
              <NavLink className={classes.navItems} to="/products">
                Products
              </NavLink>
              <NavLink className={classes.navItems} to="/services">
                Services
              </NavLink>
              <NavLink className={classes.navItems} to="about">
                About Us
              </NavLink>
            </>
          )}
           
        </Toolbar>
        <Typography className={classes.Logo} variant="h4">
            Flicker Trinket
          </Typography>
          <Button className={classes.btn} variant="contained">signUp</Button>
      </AppBar>
     
    </div>
  );
};

export default Navbar;
