import { React, useState } from "react";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import { makeStyles, Drawer, IconButton, List,Paper, Box,Container} from "@material-ui/core";
import HomeRoundedIcon from "@material-ui/icons/HomeRounded";
import MenuIcon from "@material-ui/icons/Menu";
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";
import InfoIcon from "@material-ui/icons/Info";
import CloseIcon from '@material-ui/icons/Close';


const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  drawer: {
    width: drawerWidth,
    
  },
  root: {
      width: '100vw',
      height: '100vh',
      display:'grid',
      justifyItems:'center',
      paddingTop:'10vh',
    
  },
  ListCenter:{
    display:'grid',
    alignItems:'center',
  }
}));

const DrawerComponent = () => {
  const classes = useStyles();

  const [openDrawer, setopenDrawer] = useState(false);
  return (
    <div>
      <Drawer
        anchor="right"
        onClose={() => setopenDrawer(false)}
        open={openDrawer}
      >
          <IconButton>
                  <CloseIcon/>
              </IconButton>
          <Paper component={Box} className={classes.root}>
              
        <List className={classes.drawer}>
          <ListItem button className={classes.ListCenter}>
            <ListItemIcon>
              <HomeRoundedIcon />
            </ListItemIcon> 
            <ListItemText primary="Home" />
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <ShoppingBasketIcon />
            </ListItemIcon>
            <ListItemText primary="Products" />
          </ListItem>
          <ListItem button>
            <ListItemIcon></ListItemIcon>
            <ListItemText primary="Services" />
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <InfoIcon />
            </ListItemIcon>
            <ListItemText primary="About Us" />
          </ListItem>
        </List>
        </Paper>
      </Drawer>
      <IconButton color="inherit" onClick={() => setopenDrawer(!openDrawer)}>
        <MenuIcon />
      </IconButton>
    </div>
  );
};

export default DrawerComponent;
