import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import "../index.css";
import { makeStyles, Typography,useMediaQuery,useTheme,IconButton} from "@material-ui/core";
import { NavLink } from "react-router-dom";
import DrawerComponent from './DrawerComponent';
import {MenuIcon} from '@material-ui/icons'



const useStyles = makeStyles({
  nav: {
    backgroundColor: "palevioletred",
    display:'flex',
   
  },
  Logo: {
    fontFamily: "Kaushan Script",
    flexGrow:'1'
  },
  navItems:{
    textDecoration:'none',
    
    display:'block',
    paddingRight:'15px',
    paddingLeft:'15px',
    color:"#fefefe",
    fontFamily:'Ubuntu'
  },
  
});

const Navbar = () => {
  const classes = useStyles();

  // responsive breaker points

  const  theme = useTheme();

  const isMatch = useMediaQuery(theme.breakpoints.down('sm'));
   

  

  console.log (theme)
  return (
    <div>
      <AppBar className={classes.nav}>
        <Toolbar>
          <Typography className={classes.Logo} variant="h4">
            Flicker Trinket
          </Typography>
         {isMatch ? <MenuIcon/> : (<>
         <NavLink className={classes.navItems} to='/'>
            Home
          </NavLink>
          <NavLink className={classes.navItems} to='/products'>
            Products
          </NavLink>
          <NavLink className={classes.navItems} to='/services'>
            Services
          </NavLink>
          <NavLink className={classes.navItems} to='about'>
            About Us
          </NavLink>
          </>)
          }
          
        </Toolbar>
      </AppBar>
    </div>
  );
};

export default Navbar;
