import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import "../index.css";
import { makeStyles, Typography } from "@material-ui/core";
import { NavLink } from "react-router-dom";

const useStyles = makeStyles({
  nav: {
    backgroundColor: "palevioletred",
    display:'flex'
  },
  fontLogo: {
    fontFamily: "Kaushan Script",
  },
  navItems:{
    textDecoration:'none',
    display:'inline-block',
    padding:'24px',
    flexGrow:'1'
  }
});

const Navbar = () => {
  const classes = useStyles();
  return (
    <div>
      <AppBar className={classes.nav}>
        <Toolbar>
          <Typography className={classes.fontLogo} variant="h4">
            Flicker Trinket
          </Typography>
          <NavLink className={classes.navItems} to='/'>
            Home
          </NavLink>
          <NavLink className={classes.navItems} to='/products'>
            Products
          </NavLink>
          <NavLink className={classes.navItems} to='/services'>
            Services
          </NavLink>
          <NavLink className={classes.navItems} to='about'>
            About Us
          </NavLink>
        </Toolbar>
      </AppBar>
    </div>
  );
};

export default Navbar;
